export const copyToClipBoard = (data: string, cb: () => void) => {
  navigator &&
    navigator.clipboard &&
    navigator.clipboard.writeText(data).then(() => {
      cb();
    });
};

export const useBoundingClientRect = (allowFastDom?: boolean) => {
  const [boundingRect, setBoundingRect] = useState({});

  const elemRef = useRef<HTMLElement>();

  const handleMesure = useCallback(() => {
    if (elemRef.current) {
      if (allowFastDom) {
        fastdom.measure(() => {
          setBoundingRect(elemRef.current.getBoundingClientRect() || {});
        });
      } else {
        setBoundingRect(elemRef.current.getBoundingClientRect() || {});
      }
    }
  }, [allowFastDom]);

  useEffect(() => {
    if (elemRef.current) {
      handleMesure();
      window.addEventListener('resize', handleMesure);
    }

    return () => {
      window.removeEventListener('resize', handleMesure);
    };
  }, [handleMesure]);

  return {
    elemRef: elemRef,
    boundingRect: boundingRect,
  };
};

